from itertools import count
from collections import defaultdict, deque

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = defaultdict(list)
        vis = [-1] * n

        def bfs(i: int) -> int:
            q = deque([0])
            vis[0] = i
            for step in count(1):
                tmp = q
                q = deque()
                while tmp:
                    x = tmp.popleft()
                    for y in g[x]:
                        if y == n - 1:
                            return step
                        if vis[y] != i:
                            vis[y] = i
                            q.append(y)
            return -1

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs(i)
        return ans
