class Solution:
    def maximumScoreAfterOperations(self, edges: List[List[int]], nums: List[int]) -> int:
        n = len(nums)
        path = [[] for _ in range(n)]
        for u, v in edges:
            path[u].append(v)
            path[v].append(u)

        def dfs(u, p = -1):
            dp0, dp1 = 0, 0
            f = 0
            for v in path[u]:
                if v != p:
                    v0, v1 = dfs(v, u)
                    dp1 = max(dp1, max(dp0 + v1, dp1 + v0))
                    dp0 += v0
                    f = 1
            if not f: return nums[u], 0
            return dp0, max(dp1 + nums[u], dp0)

        return dfs(0)[1]
