class Solution:
    def maximumPoints(self, edges: List[List[int]], coins: List[int], k: int) -> int:
        # Graph to store the undirected tree
        g = [[] for _ in coins]
        for x, y in edges:
            g[x].append(y)
            g[y].append(x)

        @cache
        def dfs(i: int, j: int, fa: int) -> int:
            # Two ways to collect coins as described
            res1 = (coins[i] >> j) - k  # Subtract k points after collecting all coins
            res2 = coins[i] >> (j + 1)  # Get floor(coins / 2) points

            # Loop through the children of the current node
            for ch in g[i]:
                if ch != fa:  # Avoid revisiting the parent node
                    res1 += dfs(ch, j, i)  # Continue in the same state

                    if j < 13:  # Check to prevent overflow on right shift
                        res2 += dfs(ch, j + 1, i)  # Proceed to the next state where coins are halved

            # Return the maximum of the two results
            return max(res1, res2)

        # Start DFS from the root node with initial state
        return dfs(0, 0, -1)
