from collections import deque
from typing import List

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[i + 1] for i in range(n - 1)]
        g.append([])  # To ensure the graph includes the last node without outgoing edges.

        def bfs() -> int:
            q = deque([0])
            vis = [-1] * n
            vis[0] = 0
            while q:
                node = q.popleft()
                for nei in g[node]:
                    if vis[nei] == -1:
                        vis[nei] = vis[node] + 1
                        q.append(nei)
                        if nei == n - 1:
                            return vis[nei]
            return -1

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs()
        return ans
