from sortedcontainers import SortedList
from typing import List

class FenwickTree:
    def __init__(self, n: int):
        self.s = [0] * (n + 1)
        self.n = n

    def lowbit(self, x: int) -> int:
        return x & -x

    def update(self, pos: int, v: int) -> None:
        while pos <= self.n:
            self.s[pos] += v
            pos += self.lowbit(pos)

    def query(self, pos: int) -> int:
        ret = 0
        while pos > 0:
            ret += self.s[pos]
            pos -= self.lowbit(pos)
        return ret

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        n = len(a)
        sl = SortedList()
        t = FenwickTree(n)

        def update(i: int, op: int) -> None:
            idx = sl.bisect_left(i)
            pre = sl[idx - 1]
            nxt = sl[idx % len(sl)]

            t.update((nxt - pre - 1) % n + 1, -op)
            t.update((i - pre) % n, op)
            t.update((nxt - i) % n, op)

        def add(i: int) -> None:
            if not sl:
                t.update(n, 1)
            else:
                update(i, 1)
            sl.add(i)

        def remove(i: int) -> None:
            sl.remove(i)
            if not sl:
                t.update(n, -1)
            else:
                update(i, -1)

        for i, c in enumerate(a):
            if c == a[(i + 1) % n]:
                add(i)

        ans = []
        for q in queries:
            if q[0] == 1:
                if not sl:
                    ans.append(n)
                else:
                    cnt, s = t.query(q[1]), 0
                    ans.append(s - cnt * (q[1] - 1))
            else:
                i, c = q[1], q[2]
                if a[i] == c:
                    continue
                pre, nxt = (i - 1) % n, (i + 1) % n
                if a[pre] == a[i]:
                    remove(pre)
                if a[i] == a[nxt]:
                    remove(i)
                a[i] = c
                if a[pre] == a[i]:
                    add(pre)
                if a[i] == a[nxt]:
                    add(i)
        return ans
