from sortedcontainers import SortedList
from typing import List

class FenwickTree:
    def __init__(self, n: int):
        self.s = [0] * (n + 1)

    def update(self, i: int, delta: int):
        while i < len(self.s):
            self.s[i] += delta
            i += i & -i

    def query(self, i: int) -> int:
        res = 0
        while i > 0:
            res += self.s[i]
            i -= i & -i
        return res


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n, res, d = len(colors), [], {}
        Ft = FenwickTree(n)
        for i in range(n):
            Ft.update(i + 1, 1)
            d[i] = 1
        colors.append(colors[0])
        for i in range(n):
            if colors[i] == colors[i + 1]:
                Ft.update(i + 1, -1)
                d[i] = 0
        for q in queries:
            if q[0] == 1:
                res.append(Ft.query(min(q[1], n)))
            else:
                index, color = q[1], q[2]
                for i in [index, (index - 1 + n) % n]:
                    if d[i] and colors[i] == colors[(i + 1) % n]:
                        Ft.update(i + 1, -1)
                        d[i] = 0
                    elif not d[i] and colors[i] != colors[(i + 1) % n]:
                        Ft.update(i + 1, 1)
                        d[i] = 1
                colors[index] = color
                for i in [index, (index - 1 + n) % n]:
                    if colors[i] == colors[(i + 1) % n]:
                        if d[i]:
                            Ft.update(i + 1, -1)
                            d[i] = 0
                    else:
                        if not d[i]:
                            Ft.update(i + 1, 1)
                            d[i] = 1
        return res
