class Solution:
    def maximumScoreAfterOperations(self, edges: List[List[int]], values: List[int]) -> int:
        n = len(values)
        graph = [[] for _ in range(n)]
        for u, v in edges:
            graph[u].append(v)
            graph[v].append(u)

        def dfs(current, parent=-1):
            maxPath = 0
            total = 0
            for neighbor in graph[current]:
                if neighbor == parent:
                    continue
                subtreeMaxPath, subtreeTotal = dfs(neighbor, current)
                total += subtreeTotal
                maxPath = max(maxPath, subtreeMaxPath)

            total += values[current]
            if total == 0:
                return (maxPath, 0)  # Do not add this node to path to keep tree healthy
            return (max(maxPath, total), total)

