class Solution:
    def minSum(self, nums1: List[int], nums2: List[int]) -> int:
        sum1, sum2 = sum(nums1), sum(nums2)
        zero1 = sum(1 for num in nums1 if num == 0)
        zero2 = sum(1 for num in nums2 if num == 0)

        if zero1 == 0 and zero2 == 0:
            return sum1 if sum1 == sum2 else -1
        if sum1 < sum2:
            sum1, sum2 = sum2, sum1
            zero1, zero2 = zero2, zero1
        needed_sum = sum1 - sum2
        if needed_sum == 0:
            return sum1 + max(zero1, zero2)
        if needed_sum > zero2 or (zero1 - zero2) % 2 != needed_sum % 2:
            return -1
        return sum1 + max(zero1, zero2) - (needed_sum - zero2) // 2
