class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def pushup(o: int) -> None:
            sum[o] = sum[o * 2] + sum[o * 2 + 1]

        def pushdown(o: int, l: int, r: int) -> None:
            if todo[o]:
                m = (l + r) // 2
                do(o * 2, l, m, todo[o])
                do(o * 2 + 1, m + 1, r, todo[o])
                todo[o] = 0

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L <= l and r <= R:
                val = sum[o]
                do(o, l, r, 1)
                return val
            pushdown(o, l, r)
            m = (l + r) // 2
            val = 0
            if L <= m:
                val += query_and_add1(o * 2, l, m, L, R)
            if R > m:
                val += query_and_add1(o * 2 + 1, m + 1, r, L, R)
            pushup(o)
            return val

        ans = s = 0
        last = {}
        for i, x in enumerate(nums, 1):
            j = last.get(x, 0)
            s += query_and_add1(1, 1, n, j + 1, i) * 2 + i - j
            ans += s
            last[x] = i
        return ans % 1_000_000_007
