        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L <= l and r <= R:
                do(o, l, r, 1)
                return r - l + 1
            m = (l + r) // 2
            if todo[o]:
                do(o * 2, l, m, todo[o])
                do(o * 2 + 1, m + 1, r, todo[o])
                todo[o] = 0
            ans = 0
            if L <= m:
                ans += query_and_add1(o * 2, l, m, L, R)
            if m < R:
                ans += query_and_add1(o * 2 + 1, m + 1, r, L, R)
            sum[o] = sum[o * 2] + sum[o * 2 + 1]
            return ans

        answer = 0
        finger = {}
        for num in nums:
            if num in finger:
                query_and_add1(1, 1, n, finger[num] + 1, n)
            finger[num] = nums.index(num)
            answer += query_and_add1(1, 1, n, finger[num] + 1, n) ** 2
        return answer
