            s = set()
            for i in range(len(a) - 1):
                for j in range(i + 1, len(a)):
                    if a[j] - a[i] > mx:
                        break
                    s.add(a[j] - a[i])
            return s

        hFences = [1] + hFences + [m]
        vFences = [1] + vFences + [n]
        hs = f(hFences, n)
        vs = f(vFences, m)
        common = hs & vs

        return max(common, default=-1) % MOD
