class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        if source == target:
            return 0

        # Building a dictionary for transition cost
        cost_map = {}
        for o, c, co in zip(original, changed, cost):
            if (o, c) not in cost_map or co < cost_map[(o, c)]:
                cost_map[(o, c)] = co

        min_cost = 0
        for s_char, t_char in zip(source, target):
            if s_char == t_char:
                continue
            elif (s_char, t_char) in cost_map:
                min_cost += cost_map[(s_char, t_char)]
            else:
                return -1

        return min_cost
