from typing import List
from functools import lru_cache
import sys

class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        # Convert lists to a dictionary for mapping purposes
        changes_dict = {}
        for o, c, co in zip(original, changed, cost):
            if (o, c) not in changes_dict or changes_dict[(o, c)] > co:
                changes_dict[(o, c)] = co

        @lru_cache(None)
        def dfs(source_index: int, target_index: int) -> int:
            # If we have converted whole source string successfully
            if source_index == len(source) and target_index == len(target):
                return 0
            # If we finished converting the source but target is not completed
            if source_index == len(source):
                return sys.maxsize
            # If we've finished the target and have some source left
            if target_index == len(target):
                return 0

            min_cost = sys.maxsize
            for end_index in range(source_index + 1, len(source) + 1):
                # Look for the substring in the changes dictionary
                if (source[source_index:end_index], target[target_index:target_index + (end_index - source_index)]) in changes_dict:
                    # Calculate the cost of replacing this substring, then recursively calculate the cost of the remainder
                    cost_of_operation = changes_dict[(source[source_index:end_index], target[target_index:target_index + (end_index - source_index)])]
                    subsequent_cost = dfs(end_index, target_index + (end_index - source_index))
                    # Update the minimum possible cost
                    min_cost = min(min_cost, cost_of_operation + subsequent_cost)

            return min_cost

        ans = dfs(0, 0)
        return ans if ans != sys.maxsize else -1
