class Solution {
public:
    int minimumCost(vector<int> &start, vector<int> &target, vector<vector<int>> &specialRoads) {
        using LL = long long;
        LL t = (LL) target[0] << 32 | target[1];
        unordered_map<LL, int> dis = {{(LL) start[0] << 32 | start[1], 0}, {t, INT_MAX}};
        unordered_set<LL> vis;

        for (;;) {
            LL v = -1;
            int dv = -1;
            for (auto &[p, d] : dis)
                if (!vis.count(p) && (dv < 0 || d < dv))
                    v = p, dv = d;
            if (v == t || v == -1) return dv; // 到终点的最短路已确定 or all vertices visited
            vis.insert(v);
            int vx = v >> 32, vy = v & UINT32_MAX;
            // 更新到终点的最短路
            for (auto &r: specialRoads) {
                if (r[0] == vx && r[1] == vy) {
                    int d = dv + r[4]; // Current cost + special road cost
                    LL w = (LL) r[2] << 32 | r[3];
                    if (!dis.count(w) || d < dis[w])
                        dis[w] = d;
                }
                // Add normal road cost
                LL w = (LL) target[0] << 32 | target[1];
                int d = dv + abs(target[0] - vx) + abs(target[1] - vy);
                if (!dis.count(w) || d < dis[w])
                    dis[w] = d;
            }
        }
    }
};
