class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        for (int i = s.length() - 1; i >= 0; --i) {
            unordered_set<char> blockedCharacters;
            for (int j = 1; j <= 2; ++j) {
                if (i - j >= 0) {
                    blockedCharacters.insert(s[i - j]);
                }
            }
            for (int j = 1; j <= k - (s[i] - 'a' + 1); ++j) {
                if (blockedCharacters.find(s[i] + j) == blockedCharacters.end()) {
                    return generate(s, i, j);
                }
            }
        }
        return "";
    }

    string generate(string s, int idx, int offset) {
        s[idx] += offset;
        for (int i = idx + 1; i < s.length(); ++i) {
            unordered_set<char> blockedCharacters;
            for (int j = 1; j <= 2; ++j) {
                if (i - j >= 0) {
                    blockedCharacters.insert(s[i - j]);
                }
            }
            for (char ch = 'a'; ch < 'a' + k; ++ch) {
                if (blockedCharacters.find(ch) == blockedCharacters.end()) {
                    s[i] = ch;
                    break;
                }
            }
        }
        return s;
    }
};
