class Solution {
public:
    int minimumTotalPrice(int n, vector<vector<int>>& edges, vector<int>& price, vector<vector<int>>& trips) {
        vector<vector<int>> next(n);
        for (auto &edge : edges) {
            next[edge[0]].push_back(edge[1]);
            next[edge[1]].push_back(edge[0]);
        }
        vector<int> count(n);
        function<bool(int, int, int)> dfs = [&](int node, int parent, int end) -> bool {
            if (node == end) {
                count[node]++;
                return true;
            }
            for (int child : next[node]) {
                if (child == parent) {
                    continue;
                }
                if (dfs(child, node, end)) {
                    count[node]++;
                    return true;
                }
            }
            return false;
        };
        for (auto &trip : trips) {
            dfs(trip[0], -1, trip[1]);
        }
        function<pair<int, int>(int, int)> dp = [&](int node, int parent) -> pair<int, int> {
            pair<int, int> res = {0, price[node]};
            for (int child : next[node]) {
                if (child == parent) {
                    continue;
                }
                auto [x, y] = dp(child, node);
                res.first += min(x, y);  // node 没有减半，因此可以取子树的两种情况的最小值
                res.second += x; // node 减半，只能取子树没有减半的情况
            }
            if (count[node] > 0) {
                // 如果这个节点被经过多次，价格加倍减去减半的价格
                res.second += res.first + (count[node] - 1) * price[node];
                res.first += count[node] * price[node];
            } else {
                // 如果没有被经过, 选择减半和不减半中的最小值
                res.second += res.first;
                res.first += price[node];
            }
            return res;
        };
        auto [x, y] = dp(0, -1);
        return min(x, y);
    }
};
