class Solution {
public:
    TreeNode* replaceValueInTree(TreeNode* root) {
        vector<TreeNode*> q = {root};
        root->val = 0; // Initially, root does not have any cousins.
        while (!q.empty()) {
            vector<TreeNode*> q2;
            int sum = 0; // Sum of all nodes at the current level.
            for (auto fa : q) {
                if (fa->left) {
                    q2.push_back(fa->left);
                    sum += fa->left->val;
                }
                if (fa->right) {
                    q2.push_back(fa->right);
                    sum += fa->right->val;
                }
            }
            // Assigning the sum of cousin values to each node.
            for (auto fa : q) {
                int child_sum = (fa->left ? fa->left->val : 0) + (fa->right ? fa->right->val : 0);
                if (fa->left) {
                    fa->left->val = sum - child_sum;
                }
                if (fa->right) {
                    fa->right->val = sum - child_sum;
                }
            }
            q = move(q2);
        }
        return root;
    }
};
