class Solution {
    public int numberOfSubstrings(String S) {
        char[] s = S.toCharArray();
        int n = s.length;
        int m = 0;
        int[] a = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (s[i] == '0') {
                a[m++] = i;
            }
        }
        int tot1 = n - m;
        a[m] = n; // sentinel
        int ans = 0;
        int i = 0; // the first 0 index >= left is a[i]
        for (int left = 0; left < n; left++) {
            if (s[left] == '1') {
                ans += a[i] - left; // number of substrings without 0
            }
            for (int k = i; k < m; k++) {
                int cnt0 = k - i + 1;
                if (a[k] - left - (k - i) < cnt0 * cnt0) {
                    break;
                }
                int cnt1 = a[k] - left - (k - i);
                ans += a[k + 1] - a[k];
            }
            if (s[left] == '0') {
                i++; // this 0 and after will not be enumerated again
            }
        }
        return ans;
    }
}
