class Solution {
    private static final int MX = 31622;
    private static final int[] PI = new int[MX + 1];

    static {
        for (int i = 2; i <= MX; i++) {
            if (PI[i] == 0) { // i 是质数
                PI[i] = PI[i - 1] + 1;
                for (int j = i * i; j <= MX; j += i) {
                    PI[j] = -1; // 标记 i 的倍数为合数
                }
            } else {
                PI[i] = PI[i - 1];
            }
        }
    }

    public int nonSpecialCount(int l, int r) {
        int lower_p = (int)Math.ceil(Math.sqrt(l));
        int upper_p = (int)Math.floor(Math.sqrt(r));
        if (upper_p > MX) upper_p = MX;
        if (lower_p < 2) lower_p = 2;
        int primeCount = PI[upper_p] - (lower_p > 1 ? PI[lower_p - 1] : 0);
        return (r - l + 1) - primeCount;
    }
}
