    def __init__(self, grid: List[List[int]]):
        n = len(grid)
        s = [[0, 0] for _ in range(n * n)]
        for i, row in enumerate(grid):
            for j, v in enumerate(row):
                for k, (dx, dy) in enumerate(DIRS):
                    x, y = i + dx, j + dy
                    if 0 <= x < n and 0 <= y < n:
                        s[v][k // 4] += grid[x][y]
        self.s = s

