from typing import List
from collections import deque

class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        g = [[i + 1] for i in range(n - 1)] + [[]]
        vis = [-1] * n

        def bfs(i: int) -> int:
            q = deque([0])
            vis[:] = [-1] * n
            vis[0] = 0
            while q:
                u = q.popleft()
                for v in g[u]:
                    if vis[v] == -1:
                        vis[v] = vis[u] + 1
                        q.append(v)
                        if v == n - 1:
                            return vis[v]
            return vis[n - 1]

        ans = [0] * len(queries)
        for i, (l, r) in enumerate(queries):
            g[l].append(r)
            ans[i] = bfs(i)
        return ans
