from sortedcontainers import SortedList
from typing import List

class FenwickTree:
    def __init__(self, size):
        self.size = size + 1
        self.tree = [0] * (self.size + 1)

    def update(self, index, delta):
        while index <= self.size:
            self.tree[index] += delta
            index += index & -index

    def prefix_sum(self, index):
        res = 0
        while index > 0:
            res += self.tree[index]
            index -= index & -index
        return res

    def query(self, index):
        freq = self.prefix_sum(index) - self.prefix_sum(index - 1)
        s = self.prefix_sum(index)
        return (freq, s)

class Solution:
    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        n = len(a)
        sl = SortedList()
        t = FenwickTree(n)

        # op=1，添加一个结束位置 i
        # op=-1，移除一个结束位置 i
        def update(i: int, op: int) -> None:
            idx = sl.bisect_left(i)
            pre = sl[idx - 1] if idx > 0 else sl[-1]
            nxt = sl[idx % len(sl)]

            old_length = (nxt - pre) if nxt > pre else (nxt + n - pre)
            t.update(old_length, -op)
            new_length1 = (i - pre) if i > pre else (i + n - pre)
            new_length2 = (nxt - i) if nxt > i else (nxt + n - i)
            t.update(new_length1, op)
            t.update(new_length2, op)

        # 添加一个结束位置 i
        def add(i: int) -> None:
            if not sl:
                t.update(n, 1)
            else:
                update(i, 1)
            sl.add(i)

        # 移除一个结束位置 i
        def remove(i: int) -> None:
            sl.remove(i)
            if not sl:
                t.update(n, -1)
            else:
                update(i, -1)

        for i, c in enumerate(a):
            if c == a[(i + 1) % n]:
                add(i)  # i 是一个结束位置

        ans = []
        for q in queries:
            if q[0] == 1:
                sizei = q[1]
                if not sl:
                    if sizei == n:
                        ans.append(1)
                    else:
                        ans.append(0)
                else:
                    cnt, s = t.query(sizei)
                    ans.append(s - cnt * (sizei - 1))
            else:
                i, c = q[1], q[2]
                if a[i] == c:
                    continue
                pre, nxt = (i - 1) % n, (i + 1) % n
                # 修改前，先去掉结束位置
                if a[pre] == a[i]:
                    remove(pre)
                if a[i] == a[nxt]:
