from sortedcontainers import SortedList
from typing import List

class FenwickTree:
    def __init__(self, size):
        self.n = size
        self.tree = [0] * (self.n + 1)

    def update(self, index, delta):
        index += 1
        while index <= self.n:
            self.tree[index] += delta
            index += index & -index

    def query(self, index):
        res = 0
        index += 1
        while index > 0:
            res += self.tree[index]
            index -= index & -index
        return res

    def range_query(self, left, right):
        return self.query(right) - self.query(left - 1)

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        # Initialize transitions: 1 if current and next are different, else 0
        transitions = [1 if colors[i] != colors[(i + 1) % n] else 0 for i in range(n)]
        ft = FenwickTree(n)
        for i in range(n):
            ft.update(i, transitions[i])

        result = []
        for query in queries:
            if query[0] == 1:
                size = query[1]
                if size == 1:
                    result.append(n)
                    continue
                count = 0
                for i in range(n):
                    # Check if all transitions in the window are 1
                    end = (i + size - 1) % n
                    if i + size - 1 < n:
                        total = ft.range_query(i, i + size - 2)
                    else:
                        total = ft.range_query(i, n - 1) + ft.range_query(0, (i + size - 2) % n)
                    if total == size - 1:
                        count += 1
                result.append(count)
            elif query[0] == 2:
                index, new_color = query[1], query[2]
                if colors[index] == new_color:
                    continue
                # Update colors
                colors[index] = new_color
                # Update transitions for index and (index - 1)
                left = (index - 1 + n) % n
                right = index
                new_transition_left = 1 if colors[left] != colors[index] else 0
                new_transition_right = 1 if colors[index] != colors[(index + 1) % n] else 0
                # Update left transition
                if transitions[left] != new_transition_left:
                    ft.update(left, new_transition_left - transitions[left])
                    transitions[left] = new_transition_left
                # Update right transition
                if transitions[right] != new_transition_right:
                    ft.update(right, new_transition_right - transitions[right])
                    transitions[right] = new_transition_right
        return result
