class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        INF = float('inf')
        dist = [[INF for _ in range(26)] for _ in range(26)]

        for c in range(26):
            dist[c][c] = 0

        for o, ch, co in zip(original, changed, cost):
            o_idx = ord(o) - ord('a')
            ch_idx = ord(ch) - ord('a')
            if co < dist[o_idx][ch_idx]:
                dist[o_idx][ch_idx] = co

        for k in range(26):
            for i in range(26):
                for j in range(26):
                    if dist[i][k] + dist[k][j] < dist[i][j]:
                        dist[i][j] = dist[i][k] + dist[k][j]

        total = 0
        for s, t in zip(source, target):
            if s == t:
                continue
            s_idx = ord(s) - ord('a')
            t_idx = ord(t) - ord('a')
            if dist[s_idx][t_idx] == INF:
                return -1
            total += dist[s_idx][t_idx]

        return total
