from functools import cache
from math import inf

class Solution:
    def minimumCost(self, source: str, target: str, original: List[str], changed: List[str], cost: List[int]) -> int:
        operations = {}
        for o, c, cost_ in zip(original, changed, cost):
            if o not in operations:
                operations[o] = []
            operations[o].append((c, cost_))

        @cache
        def dfs(i: int) -> int:
            if i == 0:
                return 0
            res = inf
            for j in range(i):
                s = source[j:i]
                t = target[j:i]
                if s == t:
                    res = min(res, dfs(j))
                elif s in operations:
                    for changed_s, c in operations[s]:
                        if changed_s == t:
                            res = min(res, dfs(j) + c)
            return res

        ans = dfs(len(source))
        return ans if ans < inf else -1
