#include <string>
#include <unordered_set>
using namespace std;

class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        int n = s.length();
        for (int i = n - 1; i >= 0; --i) {
            // Attempt to increment the character at position i
            for (char c = s[i] + 1; c < 'a' + k; ++c) {
                // Check if this character does not form a palindrome
                if ((i == 0 || c != s[i - 1]) && (i < 1 || (i < 2 || c != s[i - 2]))) {
                    // Create a new string with the updated character
                    string candidate = s;
                    candidate[i] = c;
                    // Update the rest of the string to the smallest possible characters
                    for (int j = i + 1; j < n; ++j) {
                        for (char ch = 'a'; ch < 'a' + k; ++ch) {
                            if ((j == 0 || ch != candidate[j - 1]) && (j < 1 || (j < 2 || ch != candidate[j - 2]))) {
                                candidate[j] = ch;
                                break;
                            }
                        }
                    }
                    return candidate;
                }
            }
        }
        return "";
    }
};
