        int tmp = nums[0];
        ans.push_back((long long)nums[0] * 2);
        for (int i = 1; i < nums.size(); ++i) {
            if (nums[i] > tmp) tmp = nums[i];
            ans.push_back(ans.back() + (long long)nums[i] + tmp);
