class Solution {
public:
    int minimizeMax(vector<int> &nums, int p) {
        sort(nums.begin(), nums.end());
        int left = -1, right = nums.back() - nums[0]; // Open interval
        while (left + 1 < right) { // Open interval
            int mid = left + (right - left) / 2;
            int cnt = 0;
            for (int i = 0; i < nums.size() - 1; ++i)
                if (nums[i + 1] - nums[i] <= mid) { // Check difference
                    ++cnt;
                    ++i;
                }
            (cnt >= p ? right : left) = mid;
        }
        return right;
    }
};
