class Solution {
public:
    vector<int> minReverseOperations(int n, int p, vector<int> &banned, int k) {
        unordered_set<int> ban{banned.begin(), banned.end()};
        set<int> sets[2];
        for (int i = 0; i < n; ++i)
            if (ban.find(i) == ban.end() && i != p)
                sets[i % 2].insert(i);
        sets[0].insert(n);
        sets[1].insert(n); // sentinel
        vector<int> ans(n, -1);
        vector<int> q = {p};
        for (int step = 0; !q.empty(); ++step) {
            vector<int> nq;
            for (int i: q) {
                ans[i] = step;
                // From mn to mx all positions can be reversed to
                int mn = max(i - k + 1, 0);
                int mx = min(i + k - 1, n - 1);
                auto &s = sets[mn % 2];
                for (auto it = s.lower_bound(mn); it != s.end() && *it <= mx; it = s.erase(it))
                    nq.push_back(*it);
            }
            q = move(nq);
        }
        return ans;
    }
};
```
